from DataStream.ByteStream import ByteStreamfrom Logic.Player import Playerfrom DataBase.DataBase import DataBaseclass LeaderboardMessage(ByteStream):    def __init__(self, client, player):        super().__init__(client)        self.id = 24403        self.player = player    def encode(self):    	plrs = DataBase.loadAll("trophies")    	count = 0    	if len(plrs) >= 200:    		count = 200    	else:    		count = len(plrs)    	gration = 0    	self.writeVInt(1)    	self.writeVInt(0)    	#self.writeVInt(0)    	self.writeString("RU")    	    	self.writeVInt(len(plrs))    	for data in range(count):    		if plrs[data]["info"]["lowID"] == self.player.LowID:    			gration += plrs.index(plrs[data]) + 1    		self.writeVInt(0)    		self.writeVInt(plrs[data]["info"]["lowID"])    		self.writeVInt(1)    		self.writeVInt(plrs[data]["info"]["trophies"])    		self.writeVInt(1)    		self.writeString(plrs[data]["info"]["name"])    		self.writeString("t.me/barleyservers")    		    		self.writeVInt(0)    		self.writeVInt(28)    		self.writeVInt(plrs[data]["info"]["profileIcon"])    		self.writeVInt(0)    	self.writeVInt(0)    	self.writeVInt(gration)        	self.writeVInt(0)    	self.writeVInt(0)    	self.writeString("RU") # country