from DataStream.ByteStream import ByteStreamfrom Logic.Player import Playerfrom Logic.Helpers import Helpers as hfrom DataBase.DataBase import DataBaseclass LeaderboardBrawler(ByteStream):    def __init__(self, client, player, b):        super().__init__(client)        self.id = 24403        self.player = player                self.b = b    def encode(self):    	unlock = h.lbHelp(self.b)    	plrs = DataBase.loadAll(unlock)    	count = 0    	if len(plrs) >= 200:    		count = 200    	else:    		count = len(plrs)    	gration = 0    	self.writeVInt(0)    	self.writeVInt(16)    	self.writeVInt(self.b)    	self.writeString("RU")    	    	self.writeVInt(len(plrs))    	    	for data in range(count):    		if plrs[data]["info"]["lowID"] == self.player.LowID:    			gration += plrs.index(plrs[data]) + 1    		self.writeVInt(0)    		self.writeVInt(plrs[data]["info"]["lowID"])    		self.writeVInt(1)    		self.writeVInt(plrs[data]["info"][unlock])    		self.writeVInt(1)    		self.writeString(plrs[data]["info"]["name"])    		self.writeString("t.me/barleyservers")    		    		self.writeVInt(0)    		self.writeVInt(28)    		self.writeVInt(plrs[data]["info"]["profileIcon"])    		self.writeVInt(0)    	self.writeVInt(0)    	self.writeVInt(gration)        	self.writeVInt(0)    	self.writeVInt(0)    	self.writeString("RU") # country